                  <div class="pricing">
                      <div class="price"><span>$</span>19</div>
                      <p>Indulge in Cinematic Bliss, Get Your Ticket Today!</p>
                  </div>
                  <ul>
                      <li>Modern movies</li>
                      <li>Front seats</li>
                      <li>Equipped halls</li>
                      <li>Guaranteed reservation</li>
                      <li>Customer Support</li>
                  </ul>
                  <button [routerLink]="['/cinema']">Uncover more</button>
              </div>
          </div>
          <!--End Pricing Block-->

          <!--Pricing Block-->
          <div class="pricing-block featured col-3 wow fadeInUp" data-wow-delay="0.6s">
              <div class="pricing-block-content">
                  <h3>Music</h3>
                  <p class="pricing-sub"></p>
                  <div class="pricing">
                      <div class="price"><span>$</span>29</div>
                      <p>Let the Music Move You, Secure Your Ticket Now!</p>
                  </div>
                  <ul>
                      <li></li>
                      <li>Distinctive music</li>
                      <li>Front seats</li>
                      <li>Equipped halls</li>
                      <li>Guaranteed reservation</li>
                      <li>Customer Support</li>
                 </ul>
                 <button (click)="getMusic()">Uncover more</button>
              </div>
          </div>
          <!--End Pricing Block-->

          <!--Pricing Block-->
          <div class="pricing-block col-3 wow fadeInUp" data-wow-delay="0.8s">
