import { Injectable } from '@angular/core';
import { HttpClient } from "@angular/common/http";
import { Observable } from "rxjs";

@Injectable({
  providedIn: 'root'
})
export class ContentService {

  private readonly API_URL_GET_CINEMA = "localhost:8080/api/Cinema";
  private readonly API_URL_POST_CINEMA = "localhost:8080/api/Cinema/addCinema";
  private readonly API_URL_DELETE_CINEMA = "localhost:8080/api/Cinema/deleteById/1";
  private readonly API_URL_GET_MUSIC = "localhost:8080/api/Music";
  private readonly API_URL_GET_THEATRE = "localhost:8080/api/Theatre";

  constructor(private http: HttpClient) { }

  // CINEMA

  getCinema(): Observable<any> {
    return this.http.get<any>(this.API_URL_GET_CINEMA)}

  postCinema(body: any): Observable<any> {
    return this.http.post<any>(this.API_URL_POST_CINEMA,body);}

  // MUSIC
  getMusic(): Observable<any> {
    return this.http.get<any>(this.API_URL_GET_MUSIC)}

  postMusic(body: any): Observable<any> {
    return this.http.post<any>(this.API_URL_POST_CINEMA,body);}

  // THEATRE
  getTheatre(): Observable<any> {
    return this.http.get<any>(this.API_URL_GET_THEATRE)}

  postTheatre(body: any): Observable<any> {
    return this.http.post<any>(this.API_URL_POST_CINEMA,body);}


}
