import { Component, OnInit } from '@angular/core';
import { ContentService } from 'src/app/services/content.service';

@Component({
  selector: 'app-cinema',
  templateUrl: './cinema.component.html',
  styleUrls: ['./cinema.component.css']
})
export class CinemaComponent implements OnInit{

  // const project ={category, director};
  cinemaList: any[] = [];

  constructor(private contentService: ContentService){}
  ngOnInit(): void {
    this.getCinema();
  }

  getCinema(): void{
    this.contentService.getCinema().subscribe({
      next: (data:any[]) => {
        console.log(data);
        this.cinemaList = data;
      },
      error: (e) =>{
        console.log(e);
      }
    })
  }

  postCinema(): void {
    const body = {
    id: 70,
    director: "130",
    numberOfActors: 'A1',
    releaseYear: '3 weeks',
    category: {
    id: 1,
    },
  };
  this.contentService.postCinema(body).subscribe({
    next: (data) =>{
      alert("Cinema posted successfuly");
      this.getCinema();
    },
    error: (e) => {
      console.log(e);
    }
  })

}
}
<h1> Cinema</h1>
<button (click)="getCinema()" > Get cinema</button>
<br>
<button (click)="postCinema()" > Post cinema</button>

<ul>
  <li *ngFor="let cinema of cinemaList">
    <p> {{cinema.id}} - {{cinema.director}} - {{cinema.numberOfActors}} - {{cinema.releaseYear}} - {{cinema.category}} </p>
  </li>
</ul>
