import { Component, OnInit } from '@angular/core';
import { ContentService } from 'src/app/services/content.service';

@Component({
  selector: 'app-theatre',
  templateUrl: './theatre.component.html',
  styleUrls: ['./theatre.component.css']
})
export class TheatreComponent implements OnInit{

  // const project ={category, director};
  theatreList: any[] = [];

  constructor(private contentService: ContentService){}
  ngOnInit(): void {
    this.getTheatre();
  }

  getTheatre(): void{
    this.contentService.getTheatre().subscribe({
      next: (data:any[]) => {
        console.log(data);
        this.theatreList = data;
      },
      error: (e) =>{
        console.log(e);
      }
    })
  }


}
<app-header><
<button (click)="getTheatre()" > Get Theatre</button>
<ul>
  <li *ngFor="let theatre of theatreList">
    <p> {{theatre.id}} - {{theatre.playWright}} - {{theatre.numberOfActors}} - {{theatre.category}}  </p>
  </li>
</ul>
